def get_precision_from_type(typeStr):
    """Returns the precision bit-width of a given LLVM type
    """
    if (typeStr=="Half"):
        return 10
    elif(typeStr=="Float"):
        return 23
    elif(typeStr=="Double"):
        return 52
    elif (typeStr=="Int1"):
        return 1
    elif (typeStr=="Int8"):
        return 8
    elif (typeStr=="Int16"):
        return 16
    elif (typeStr=="Int32"):
        return 32
    elif (typeStr=="Int64"):
        return 64
    else:
        logging.error('Unrecognized type: {}'.format(typeStr))
        exit()

def get_bitwidth_from_type(typeStr):
    """Returns the bit-width of a given LLVM type
    """
    if (typeStr=="Half"):
        return 16
